jQuery(document).ready(function () {

    jQuery('.flexslider').flexslider({
        animation: "slide",
        directionNav: true,
		Touch: 1,
		controlNav: 0,
		mousewheel: false,
    });

    jQuery(".switcher").click(function (event) {
        event.preventDefault();
        autoselect(jQuery(this));
    });
    jQuery("#printPage").click(function (event) {
        event.preventDefault();
        jQuery("#printContent").printThis(
                {
                    debug: false,
                    importCSS: true,
                    importStyle: true,
                    printContainer: true,
                });
    });

    jQuery("#mobileMenuButton").click(function (event) {
        event.preventDefault();
        jQuery("#mobileMenu").toggle();
        jQuery("#main").toggle();


    });

    jQuery("<div style='clear:both;'></div>").insertAfter('table');
    jQuery('#episode').on('change', function () {

        var url = jQuery(this).val(); // get selected value
        if (url) { // require a URL
            window.location = url; // redirect
        }
        return false;
    });
    jQuery('#additionalLinks').on('change', function () {

        var url = jQuery(this).val(); // get selected value
        if (url) { // require a URL
            window.open(url, '_blank'); // redirect
        }
        return false;
    });

    jQuery(".block-menu-block h2").click(function () {
        jQuery(".block-menu-block .content").toggle();
    });
    jQuery(".fancybox").fancybox();
    jQuery("#block-locale-language .active").click(function (event) {
        event.preventDefault();
        jQuery(".languageSelector ul li").toggle();
        jQuery(".languageSelector ul .active").show();
        return false;
    });
    jQuery(".articleContent img").addClass('img-responsive');
    jQuery('.collapseMenu').click(function () {
        //jQuery('.collapseTitle').removeClass('selected');
        console.log(jQuery(this).next('.collapsible'));
        if (jQuery(this).hasClass('selected')) {
            jQuery(this).removeClass('selected');
        } else {
            jQuery(this).addClass('selected');
        }
        jQuery(this).next('.collapsible').slideToggle();
    });
    var offset = 300,
            //browser window scroll (in pixels) after which the "back to top" link opacity is reduced
            offset_opacity = 1200,
            //duration of the top scrolling animation (in ms)
            scroll_top_duration = 700,
            //grab the "back to top" link
            $back_to_top = jQuery('.cd-top');

    //hide or show the "back to top" link
    jQuery(window).scroll(function () {
        (jQuery(this).scrollTop() > offset) ? $back_to_top.addClass('cd-is-visible') : $back_to_top.removeClass('cd-is-visible cd-fade-out');
        if (jQuery(this).scrollTop() > offset_opacity) {
            $back_to_top.addClass('cd-fade-out');
        }
    });

    //smooth scroll to top
    $back_to_top.on('click', function (event) {
        event.preventDefault();
        jQuery('body,html').animate({
            scrollTop: 0,
        }, scroll_top_duration
                );
    });
    //Stickem Container
    setStickyMenu();
    jQuery('.imagesGallery').jscroll({
        loadingHtml: '<div class="galleryLoader"><img src="/sites/all/themes/lebarmy/imgs/loading.gif" alt="Loading" /></div>',
        nextSelector: '.nextScroll',
        debug: true
    });
    jQuery('.videoGallery').jscroll({
        loadingHtml: '<div class="galleryLoader"><img src="/sites/all/themes/lebarmy/imgs/loading.gif" alt="Loading" /></div>',
        nextSelector: '.nextScroll',
        debug: true
    });


});


function autoselect(clk) {
    jQuery(".switcher").removeClass("selected");
    clk.addClass("selected");
    var tg = clk.attr("target");
    jQuery(".contentMini").hide();
    jQuery('#' + tg).show();

}
function setStickyMenu() {
    jQuery(window).scroll(function () {
        if (isScrolledIntoView(".secondaryMenu")) {
            jQuery("#menuTop").removeClass("stickTopMenu");
			jQuery("#menuTopSticky").removeClass("stickyshadow");
            jQuery("#logo").removeClass("positionFix");
            jQuery(".slider").removeClass("margin90Top");
        } else {
            jQuery("#menuTop").addClass("stickTopMenu");
			jQuery("#menuTopSticky").addClass("stickyshadow");
            jQuery("#logo").addClass("positionFix");
            jQuery(".slider").addClass("margin90Top");
        }
        /*if (isScrolledIntoView("#top") || isScrolledIntoView("#bottom")) {
         jQuery(".stickem").removeClass("stickit");
         } else {
         jQuery(".stickem").addClass("stickit");
         }*/
        //jQuery('#containerStickem').stickem();
    });

}
function isScrolledIntoView(elem)
{
    var $elem = jQuery(elem);
    var $window = jQuery(window);

    var docViewTop = $window.scrollTop();
    var docViewBottom = docViewTop + $window.height();

    var elemTop = $elem.offset().top;
    var elemBottom = elemTop + $elem.height();

    return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
}
function getCookie(cname) {
    var name = cname + "=";
    var ca = document.cookie.split(';');
    for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ')
            c = c.substring(1);
        if (c.indexOf(name) == 0)
            return c.substring(name.length, c.length);
    }
    return "";
};
